	var init = function() {
		var isMobile =
		navigator.userAgent &&
		navigator.userAgent.toLowerCase().indexOf("mobile") >= 0;
		var isSmall = window.innerWidth < 1000;

		var ps = new ParticleSlider({
			sliderId: 'particle-slider',
			ptlGap: isMobile || isSmall ? 3 : 2,
			ptlSize: isMobile || isSmall ? 3 : 	2,
			monochrome: false
		});


		var ps2 = new ParticleSlider({
			sliderId: 'particle-slider2',
			ptlGap: isMobile || isSmall ? 3 : 25,
			ptlSize: isMobile || isSmall ? 3 : 	5,
			monochrome: true
		});


	};

	var initParticleSlider = (function() {
		var psScript = document.createElement("script");
		psScript.addEventListener
		? psScript.addEventListener("load", init, false)
		: (psScript.onload = init);
		psScript.src =
		"<?php echo get_template_directory_uri(); ?>/js/lib/particleslider.js";
		psScript.setAttribute("type", "text/javascript");
		document.body.appendChild(psScript);
	})(
	window.addEventListener
	? window.addEventListener("load", initParticleSlider, false)
	: (window.onload = initParticleSlider)
	);


	jQuery(document).ready(function () {
		jQuery("a.nav-link").click(function () {

	var scroll_el = jQuery(this).attr('href'); // возьмем содержимое атрибута href, должен быть селектором, т.е. например начинаться с # или .
        if (jQuery(scroll_el).length != 0) { // проверим существование элемента чтобы избежать ошибки
	    jQuery('html, body').animate({ scrollTop: jQuery(scroll_el).offset().top }, 500); // анимируем скроолинг к элементу scroll_el
	}
	    return false; // выключаем стандартное действие

	});
	});


</script>		
